import Model.*;import Model.Map.Etage;import Model.Map.Map;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.Random;public class Main {    public static void main(String[] args) throws IOException {        BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));        System.out.print("Numero seed ? ");        String seed = reader.readLine();        long seed_value;        try{            seed_value = Long.parseLong(seed);        }        catch (NumberFormatException e){            seed_value = new Random().nextLong();        }        System.out.println(seed_value);        Procedure.setSeed(seed_value);        Map map = new Map();        BasicPlayer player = map.getPlayer();        while(true){            Etage myEtage=map.getCurrent();            System.out.println(Affichage.CLEAR+myEtage);            System.out.print(Affichage.RESET+"Etage nÂ°"+(map.getIndexCurrent()+1));            System.out.println("\n\n\n\n\n\n\n\n\n\n");            System.out.print(Affichage.RESET+"Enter your key: ");            switch (reader.readLine()) {                case "z" -> player.moveUp();                case "q" -> player.moveLeft();                case "s" -> player.moveDown();                case "d" -> player.moveRight();                case "exit" -> System.exit(0);                default -> System.out.println("Wrong key");            }            switch (myEtage.get(map.getPlayer().getPosition()).getType()){                case UP :                    map.UP();                    break;                case DOWN :                    map.DOWN();                    break;                default:                    break;            }        }    }}