package Model;import Model.Entitys.BasicPlayer;import Model.Map.*;import Model.Map.Room_Strategy.NormalTriangleStrategy;import Model.Utils.*;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.Random;public class Main {    private static long seed;    private static Map map;    private static BasicPlayer player;    public static void main(String[] args) throws IOException {        BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));        Etage etage = init(reader);        TourManager tm = new TourManager(reader, player, map, etage);        while (true) {            tm.playTour();        }    }    private static Etage init(BufferedReader reader) throws IOException {        System.out.print("Numero seed ? ");        long seed_value;        try{            seed_value = Long.parseLong(reader.readLine());        }        catch (NumberFormatException e){            seed_value =new Random().nextLong();        }        seed=seed_value;        Procedure.setSeed(seed);        map = new Map();        player = map.getPlayer();        return map.getCurrent();    }    public static void affichage(Etage etage){        System.out.print(Affichage.CLEAR);        System.out.println(seed);        System.out.println(etage);        int index= map.getIndexCurrent()+1;        if (index == 0) {            System.out.print(Affichage.RESET + "Salle secrete");        } else {            System.out.print(Affichage.RESET + "Etage nÂ°" + index+"\n");        }        for(String s : TourManager.getMessages()){            System.out.println(s);        }        System.out.print("Enter your key: ");    }    public static BasicPlayer getPlayer(){        return player;    }}